import { useState } from "react" ;
import { Editor , Provider , Sidebar } from "@inngest/workflow-kit/ui" ;

import { actions } from "@/inngest/workflowActions" ;
import { type Workflow } from "@/lib/supabase/types" ;

import "@inngest/workflow-kit/ui/ui.css" ;
import "@xyflow/react/dist/style.css" ;

export const AutomationEditor = ({ workflow } : { workflow : Workflow }) => {
const [ workflowDraft , updateWorkflowDraft ] =
useState < typeof workflow>(workflow);

return (
< Provider
key = { workflowDraft ?.id}
workflow = { workflowDraft ?.workflow}
trigger = {{
event : {
name : workflowDraft .trigger ,
} ,
}}
availableActions = {actions}
onChange = {(updated) => {
updateWorkflowDraft ({
... workflowDraft ,
workflow : updated ,
});
}}
>
< Editor >
< Sidebar position = "right" />
);
};
```
is a
[Controlled Component](https://react.dev/learn/sharing-state-between-components#controlled-and-uncontrolled-components)
, relying on the
```
workflow={}
```
object to update its UI.
Every change performed by the user will trigger the
```
onChange={}
```
callback to be called. This callback should update the object passed to the
```
workflow={}
```
prop and can be used to also implement an auto save mechanism.
The complete version of the is
[available on GitHub](https://github.com/inngest/workflow-kit/blob/main/examples/nextjs-blog-cms/components/automation-editor.tsx)
.
Navigating to
```
/automation/1
```
