      },
      "RateLimit-Remaining": {
        "required": true,
        "schema": {
          "type": "string",
          "description": "Remaining requests for this endpoint for a given window. For informational purposes only\n"
        }
      },
      "RateLimit-Reset": {
        "required": true,
        "schema": {
          "type": "string",
          "description": "The fixed window in time, in seconds, which represents when a limit is fully replenished. For informational purposes only.\n"
