    "schemas": {
      "Product": {
        "type": "object",
        "required": [
          "productCode",
          "status",
          "createdAt"
        ],
        "properties": {
          "status": {
            "$ref": "#/components/schemas/ProductStatus"
          },
          "productCode": {
            "type": "string",
            "pattern": "(?s).*[\\S].*",
            "description": "Code that is the unique identifier for this product.\n\n  - Example: `\"5657BRIDGECLIMB\"`\n"
          },
          "language": {
            "type": "string",
            "description": "Code indicating the language into which the natural-language fields in this response will be translated (if necessary)"
          },
          "createdAt": {
            "type": "string",
            "format": "date-time",
            "description": "Timestamp (UTC) indicating when this product was originally created\n\n  - Example: `\"2019-04-03T02:54:29.082753Z\"`\n"
          },
          "lastUpdatedAt": {
            "type": "string",
            "format": "date-time",
            "description": "Timestamp (UTC) indicating the most recent occasion of this product's details being modified\n\n  - Example: `\"2020-06-11T09:16:39Z\"`\n"
          }
        },
        "discriminator": {
          "propertyName": "status",
          "mapping": {
            "ACTIVE": "#/components/schemas/ActiveProduct",
            "INACTIVE": "#/components/schemas/InactiveProduct"
          }
        }
      },
      "TicketType": {
        "type": "string"
      },
      "TicketsPerBooking": {
        "type": "string",
        "description": "Machine-interpretable value indicating whether each traveler needs their own ticket; one of: \n\n  - `\"ONE_PER_BOOKING\"`  \n  - `\"ONE_PER_TRAVELER\"`\n"
      },
      "TicketInfo": {
        "type": "object",
        "required": [
          "ticketTypes",
          "ticketsPerBooking"
        ],
        "description": "Ticket/voucher details for this product",
        "properties": {
          "ticketTypes": {
            "type": "array",
            "description": "Array of machine-interpretable values encoding the ticket type for this product; i.e., must be printed (`\"PAPER\"`) or can be displayed on-screen (`\"MOBILE_ONLY\"`); either or both of:\n\n  - `\"MOBILE_ONLY\"`\n  - `\"PAPER\"`\n",

"items": {
              "$ref": "#/components/schemas/TicketType"
            }
          },
          "ticketTypeDescription": {
            "type": "string",
            "description": "Description of this ticket type\n- **Note**: This field contains natural language suitable for display to the user; content will be translated (if necessary) into the language specified in the Accept-Language header parameter\n"
          },
          "ticketsPerBooking": {
            "$ref": "#/components/schemas/TicketsPerBooking"
          },
          "ticketsPerBookingDescription": {
            "type": "string",
            "description": "Description of this product's tickets-per-booking information \n- **Note**: This field contains natural language suitable for display to the user; content will be translated (if necessary) into the language specified in the Accept-Language header parameter\n"
          }
        }
      },
      "PricingType": {
        "type": "string",
        "description": "Machine-interpretable value specifying whether the pricing for this product is calculated per individual or per group (unit); one of:  \n\n  - `\"PER_PERSON\"`\n  - `\"UNIT\"`\n"
      },
      "AgeBandType": {
        "type": "string",
        "description": "Machine-interpretable value that specifies this age band's type; one of:\n\n  - `\"ADULT\"`\n  - `\"SENIOR\"`\n  - `\"YOUTH\"`\n  - `\"CHILD\"`\n  - `\"INFANT\"`\n  - `\"TRAVELER\"`\n"
      },
      "AgeBand": {
        "type": "object",
        "description": "Age range categories for this product's ticketing scheme",
        "properties": {
          "ageBand": {
            "$ref": "#/components/schemas/AgeBandType"
          },
          "startAge": {
            "type": "integer",
            "description": "Lower limit (inclusive) of the age range defined by this age band"
          },
          "endAge": {
            "type": "integer",
            "description": "Upper limit (inclusive) of the age range defined by this age band"
          },
          "minTravelersPerBooking": {
            "type": "integer",
            "description": "Minimum number of travelers in this age band required to book the product"
          },
          "maxTravelersPerBooking": {
            "type": "integer",
            "description": "Maximum Number of travelers in this age band allowed to book the product"
          }
        }
      },
      "PricingUnitType": {
        "type": "string",
        "description": "Machine-interpretable value specifying the type of unit/group when `type` is `\"UNIT\"`; one of:\n\n  - `\"BIKE\"`\n  - `\"BOAT\"`\n  - `\"GROUP\"`\n  - `\"PACKAGE\"`\n  - `\"ROOM\"`\n  - `\"AIRCRAFT\"`\n  - `\"VEHICLE\"`\n"
      },
      "PricingInfo": {
        "type": "object",
        "required": [
          "type",
          "agebands"
        ],
        "description": "Ticket/voucher details for this product",
        "properties": {
          "type": {
            "$ref": "#/components/schemas/PricingType"
          },
          "ageBands": {
            "type": "array",
            "description": "Age bands for this product",
            "items": {
              "$ref": "#/components/schemas/AgeBand"
            }
          },
          "unitType": {
            "$ref": "#/components/schemas/PricingUnitType"
          }
        }
      },
      "ImageSourceType": {
        "type": "string",
        "description": "Machine-interpretable value indicating the source of this image; one of:\n\n  - `\"SUPPLIER_PROVIDED\"`\n  - `\"PROFESSIONAL\"`\n"
      },
      "ImageVariant": {
        "type": "object",
        "required": [
          "height",
          "width",
          "url"
        ],
        "properties": {
          "height": {
            "type": "integer",
            "description": "Height of this image in pixels\n\n  - Example: `'480'`\n"
          },
          "width": {
            "type": "integer",
            "description": "Width of this image in pixels\n- Example `'720'`\n"
          },
          "url": {
            "type": "string",
            "pattern": "(?s).*[\\S].*",

"description": "URL from which this image can be retrieved.\n"
          }
        }
      },
      "Image": {
        "type": "object",
        "description": "Image information",
        "required": [
          "variants",
          "isCover"
        ],
        "properties": {
          "imageSource": {
            "$ref": "#/components/schemas/ImageSourceType"
          },
          "caption": {
            "type": "string",
            "description": "Description of this photo\n- **Note**: This field contains natural language suitable for display to the user; content will be translated (if necessary) into the language specified in the Accept-Language header parameter\n"
          },
          "isCover": {
            "type": "boolean",
            "description": "`true` if this photo is considered to be the cover (leading) photo for this product from this `imageSource`"
          },
          "variants": {
            "type": "array",
            "description": "Dimension/resolution variants available for this image",
            "items": {
              "$ref": "#/components/schemas/ImageVariant"
            }
          }
        }
      },
      "LocationReference": {
        "type": "object",
        "description": "Details about the location can be retrieved using the [/locations/bulk](#operation/locationsBulk) endpoint\n",
        "required": [
          "ref"
        ],
        "properties": {
          "ref": {
            "type": "string",
            "pattern": "(?s).*[\\S].*",
            "description": "Location identifier code\n\n  - **Example**: `\"LOC-c7d4f6ba-3633-4419-87d4-e88bb66206fc\"`\n"
          }
        }
      },
      "StartEndPoint": {
        "type": "object",
        "required": [
          "location"
        ],
        "properties": {
          "location": {
            "$ref": "#/components/schemas/LocationReference"
          },
          "description": {
            "type": "string",
            "description": "Description of this location\n\n  - **Note**: This field contains natural language suitable for display to the user; content will be translated (if necessary) into the language specified in the Accept-Language header parameter\n"
          }
        }
      },
      "Redemption": {
        "type": "object",
        "description": "Redemption point details for this product",
        "required": [
          "redemptionType"
        ],
        "properties": {
          "redemptionType": {
            "pattern": "(?s).*[\\S].*",
            "type": "string",
            "description": "Machine-interpretable value specifying the type of redemption; one of:\n\n  - `\"NONE\"` - No redemption point - this is a direct entry ticket\n  - `\"ATTRACTION_START_POINT\"` - Tickets can be redeemed at this product's starting point\n  - `\"DIFFERENT_LOCATION\"` - Tickets can be redeemed from a location other than the product's starting point\n  - `\"INDIRECT_DELIVERY\"` - Tickets will be delivered post-booking by email; or, physically delivered to the customer or the customer's hotel\n"
          },
          "locations": {
            "type": "array",
            "description": "Locations for this redemption point\n\nDetails about the location can be retrieved using the [/locations/bulk](#operation/locationsBulk) endpoint\n",
            "items": {
              "$ref": "#/components/schemas/LocationReference"
            }
          },
          "specialInstructions": {
            "type": "string",
            "description": "Special instructions pertaining to this redemption point\n\n  - **Example**: `\"Bring photo ID for entrance tickets.\"`\n\n  - **Note**: This field contains natural language suitable for display to the user; content will be translated (if necessary) into the language specified in the Accept-Language header parameter\n"
          }
        }
      },
      "PickupOptionType": {
        "type": "string",
        "description": "Machine-interpretable value encoding the type of traveler pickup option; one of:\n\n  - `\"PICKUP_EVERYONE\"` (the supplier will pick up all customers from their respective locations)\n  - `\"PICKUP_AND_MEET_AT_START_POINT\"` (the supplier will pick up all customers from their respective locations; or, customers can make their own way to the start point)\n  - `\"MEET_EVERYONE_AT_START_POINT\"` (all customers must make their own way to and meet at the start point)\n\n**Note**:\n  - This value of this field may affect whether or not some booking questions should be answered. See the section on [conditional booking questions](#section/Booking-concepts/Booking-questions) for more information.\n"

},
      "PickupLocation": {
        "type": "object",
        "description": "Pickup or meeting point",
        "required": [
          "location"
        ],
        "properties": {
          "location": {
            "$ref": "#/components/schemas/LocationReference"
          },
          "pickupType": {
            "type": "string",
            "description": "Type of pickup location; one of: \n\n  - `\"AIRPORT\"` – the item is an airport\n  - `\"HOTEL\"` – the item is a hotel\n  - `\"PORT\"` – the item is a sea port\n  - `\"LOCATION\"` – the item is a specific address added by the supplier\n  - `\"OTHER\"` – the item is a pseduo-location; one of: \n    + `\"MEET_AT_DEPARTURE_POINT\"`, or \n    + `\"CONTACT_SUPPLIER_LATER\"`\n\n**Note**: The pickup type becomes relevent when providing answers to [booking questions](#section/Booking-concepts/Booking-questions) when making a booking.\n"
          }
        }
      },
      "TravelerPickup": {
        "type": "object",
        "description": "Traveler pickup details for this product",
        "required": [
          "pickupOptionType",
          "allowCustomTravelerPickup"
        ],
        "properties": {
          "pickupOptionType": {
            "$ref": "#/components/schemas/PickupOptionType"
          },
          "allowCustomTravelerPickup": {
            "type": "boolean",
            "description": "Specifies whether travelers can choose a custom pickup or meeting location for this product\n\n  - **Note**: If 'false', answer to `\"PICKUP_POINT\"` booking question in request to [/bookings/book](#operation/bookingsBook), `unit` must be `\"LOCATION_REFERENCE\"` and not `\"FREETEXT\"`\n"
          },
          "locations": {
            "type": "array",
            "description": "Pickup and meeting points",
            "items": {
              "$ref": "#/components/schemas/PickupLocation"
            }
          },
          "minutesBeforeDepartureTimeForPickup": {
            "type": "integer",
            "description": "How long prior to the product departure time will traveler-pickup occur (in minutes)"
          },
          "additionalInfo": {
            "type": "string",
            "description": "Description of additional information regarding traveler pickup\n\n  - **Note**: This field contains natural language suitable for display to the user; content will be translated (if necessary) into the language specified in the Accept-Language header parameter\n"
          }
        }
      },
