        "type": "object",
        "description": "Logistics details for this product",
        "required": [
          "redemption"
        ],
        "properties": {
          "start": {
            "type": "array",
            "description": "Starting point locations and descriptions",
            "items": {
              "$ref": "#/components/schemas/StartEndPoint"
            }
          },
          "end": {
            "type": "array",
            "description": "Ending point locations and descriptions",
            "items": {
              "$ref": "#/components/schemas/StartEndPoint"
            }
          },
          "redemption": {
            "$ref": "#/components/schemas/Redemption"
          },
          "travelerPickup": {
            "$ref": "#/components/schemas/TravelerPickup"
          }
        }
      },
      "InclusionExclusionItem": {
