      },
      "ConfirmationType": {
        "type": "string",
        "description": "Machine-interpretable value encoding when bookings for this product will be confirmed; one of: \n\n  - `\"INSTANT\"` (instantly) \n  - `\"MANUAL\"` (requires manual action prior to confirmation)\n  - `\"INSTANT_THEN_MANUAL\"` (combination – subject to cutoff time)\n"
      },
      "BookingConfirmationSettings": {
