        ],
        "properties": {
          "minTravelersPerBooking": {
            "type": "integer",
            "description": "Minimum number of travelers required to book this product"
          },
          "maxTravelersPerBooking": {
            "type": "integer",
            "description": "Maximum number of travelers required to book this product"
          },
          "requiresAdultForBooking": {
            "type": "boolean",
            "description": "**True** if at least one passenger from the `\"ADULT\"` or `\"SENIOR\"` age band type must be included to book this product\n"
          }
        }
      },
      "LanguageGuide": {
        "type": "object",
        "description": "Specifies which language and what type of language guide to include if language guides are available for this product. \n\n**Note:** This element must be included in order to book products that offer language guides.\n",
        "required": [
          "language",
          "type"

}
        },
        "discriminator": {
          "propertyName": "itineraryType",
